package Loops;

public class WhileLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=0; 
		while (num<=10) {
			System.out.println(num);
			System.out.println("inside the loop");	
			num++;
			
		}
			
		System.out.println("after while loop");	
	}

}
